 import React, { useState } from 'react'
import Customer from './Customer'
import data from '../data'

export default function CustomerContainer() {
  const [customers, setCustomers] = useState(data.customers)
  const [filter, setFilter] = useState("")

  const filteredCustomer = customers.filter(customer => (
    customer.name.toLowerCase().includes(filter)
  ))

  const customersToDisplay = filter ? filteredCustomer : customers

  return (
    <div className='w-full container m-auto'>
      <input
        type="text"
        placeholder="Search"
        value={filter}
        onChange={e => setFilter(e.target.value.toLowerCase)}
      />
      {!filteredCustomers.length && (
        <div>No Matches</div>
      )}
      {customersToDisplay.map((customer) => (
        <Customer
          name={customer.name}
          email={customer.email}
          transactions={customer.transaction}
          key={customer._id}
        />
      ))}
    </div>
  )
}
